import re

def main(input):
    table = []
    attack_techniques = []

    for event in input:
        row = {
            "Story name": event.get("Story name"),
            "Timestamp": event.get("Timestamp"),
            "Detection Title": event.get("Detection Title"),
            "Level": event.get("Level"),
            "Description Rule": event.get("Description Rule"),
            "Command": event.get("Command"),
            "Event Type": event.get("Event Type"),
            "Device IP": event.get("Device IP"),
            "MITRE Tactic": event.get("MITRE Tactic"),
            "MITRE Techniques": event.get("MITRE Techniques")
        }
        table.append(row)

        techniques = event.get("MITRE Techniques")
        if techniques:
            for technique in re.findall(r"attack\.t\d{4}", techniques):
                attack_techniques.append(technique.replace("attack.", ""))

    return {
        "table": table,
        "attack_techniques": list(set(attack_techniques))
    }

def main(input):
    technique_ids = set()
    ability_ids = []
    for ability in input:
        technique_id = ability.get('technique_id')
        if technique_id in ['T1018', 'T1053', 'T1012', 'T1566.001', 'T1047', 'T1003']:
            technique_ids.add(technique_id)
            ability_ids.append(ability.get('ability_id'))
            if len(technique_ids) == 6:
                break
    return sorted(ability_ids)

def main(input):
    output = []
    for item in input.get('output', []):
        output.append([item.get('technique_id'), item.get('name')])

    return '\n'.join(['| Technique ID | Name |',
                     '| --- | --- |'] + 
                    [' | '.join(row) for row in output])

def main(input):
    table = []
    for item in input:
        technique_id = item.get('technique_id')
        name = item.get('name')
        if technique_id is None or name is None:
            table.append(['detected is none', 'detected is none'])
        else:
            table.append([technique_id, name])

    return '| Technique ID | Technique Name |\n|--------------|---------------|\n' + '\n'.join(f'| {id_} | {name} |' for id_, name in table)

def main(input):
    table = []
    for item in input:
        technique_id = item.get('technique_id')
        name = item.get('name')
        table.append([technique_id, name])

    header = '| Technique ID | Technique Name |'
    divider = '|:-------------|:--------------|'
    rows = ['|{}|{}|'.format(tid, name) for tid, name in table]
    return '\n'.join([header, divider] + rows)

def main(input):
    output = []
    for entry in input:
        output.append({
            "technique_id": entry.get("technique_id"),
            "name": entry.get("name")
        })
    return output

def main(input):
    detected = []
    undetected = []

    for technique in input.get('techniques', []):
        technique_id = technique.get('technique_id')
        name = technique.get('name')

        if any(detection.lower() == technique_id.lower() for detection in input.get('detections', [])):
            detected.append({'name': name, 'technique_id': technique_id})
        else:
            undetected.append({'name': name, 'technique_id': technique_id})

    result = {}
    if detected:
        result['detected'] = detected
    if undetected:
        result['undetected'] = undetected

    return result
